document.addEventListener("DOMContentLoaded",function(){document.getElementById("form-calculador").addEventListener("submit",function(e){e.preventDefault();e=(parseFloat(document.getElementById("nota1").value)+parseFloat(document.getElementById("nota2").value)+parseFloat(document.getElementById("nota3").value)+parseFloat(document.getElementById("nota4").value))/4;document.getElementById("resultado-valor").innerText=e.toFixed(2),document.querySelector(".resultado").style.display="block"})});